#!/bin/bash

## Add safestringlib external dependency
## It does not exist by default in the source code zip file auto-generated by GitHub
rm -rf $SRC_DIR/ext/safestringlib/
cd $SRC_DIR/ext/
git clone https://github.com/intel/safestringlib
cd $SRC_DIR/ext/safestringlib/
git checkout 245c4b8 ## Ensure updates to safestringlib do not break compilation

## Fix error "implicit declaration of function" when compiling safestringlib
sed -i "1i#include <stdlib.h>" $SRC_DIR/ext/safestringlib/safeclib/abort_handler_s.c
sed -i "1i#include <ctype.h>"  $SRC_DIR/ext/safestringlib/safeclib/strcasestr_s.c
sed -i "1i#include <ctype.h>"  $SRC_DIR/ext/safestringlib/safeclib/strcasecmp_s.c

## Compile
make -C $SRC_DIR

## Move binary to final location and clean up
mkdir -p $PREFIX/bin
chmod +x $SRC_DIR/bwa-plus*
mv $SRC_DIR/bwa-plus* $PREFIX/bin

## Add aliases
for binary in $PREFIX/bin/bwa-plus*; do
    ln -sf $binary $(echo $binary | sed "s/bwa-plus/bwa-mem2/")
done
